document.addEventListener("DOMContentLoaded",function(){for(var e=document.getElementsByTagName("img"),t=0;t<e.length;t++)e[t].classList.add("img-fluid");let o=document.getElementById("js-top");window.addEventListener("scroll",()=>{var e=window.scrollY;o.className=0<e?"top-link show":"top-link hide"});o.onclick=function(e){e.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})}},!1);